{% endif %}

c74object.inletlist:
{% set inlet_count=0 %}
{% if exists("inputs") %}
{% for port in inputs %}
{% if not tag(port, "class_attribute") %}
{% set inlet_count=inlet_count+1 %}

  {{ make_digest(port) }}
  {{ make_description(port) }}

{% endif %}
{% endfor %}
{% endif %}

c74object.outletlist:
{% set outlet_count=0 %}
{% if exists("outputs") %}
{% for port in outputs %}
{% set outlet_count=outlet_count+1 %}

    {{ make_digest(port) }}
    {{ make_description(port) }}

{% endfor %}
{% endif %}

c74object.methodlist:
{% if exists("messages") %}
